import React from "react";
const About = () => {
    return (
        <div>
            <h2>About Page</h2>
            <section class="about">
                <h2>About LearnHub</h2>
                <p>LearnHub is an online learning platform that provides high-quality educational content for students, professionals, and lifelong learners. Our goal is to make learning accessible, engaging, and fun.</p>

                <h3>Our Mission</h3>
                <p>To empower individuals with the knowledge and skills they need to succeed in a rapidly evolving world.</p>

                <h3>Why Choose Us?</h3>
                <ul>
                    <li>Expert instructors with real-world experience.</li>
                    <li>Interactive courses and hands-on projects.</li>
                    <li>Flexible learning schedules.</li>
                    <li>Certificate of completion for every course.</li>
                </ul>
            </section>

            <footer>
                <p>&copy; 2025 LearnHub. All Rights Reserved.</p>
            </footer>
        </div>
    );
};
export default About;
